beforeRequest.script:
// 自动设置认证头
if (pm.environment.get('TOKEN')) {
  pm.request.headers.add({
    key: 'Authorization',
    value: 'Bearer ' + pm.environment.get('TOKEN')
  });
}

// 自动设置请求ID用于追踪
pm.request.headers.add({
  key: 'X-Request-ID',
  value: pm.variables.replaceIn('{{$guid}}')
});

// 自动设置时间戳
pm.request.headers.add({
  key: 'X-Timestamp',
  value: new Date().toISOString()
});

afterResponse.script:
// 自动保存登录返回的token
if (pm.response.code === 200 && pm.request.url.path.includes('/auth/login')) {
  try {
    const response = pm.response.json();
    if (response.data && response.data.token) {
      pm.environment.set('TOKEN', response.data.token);
      pm.environment.set('USER_ID', response.data.user.id);
      console.log('Token已自动保存到环境变量');
    }
  } catch (e) {
    console.log('解析响应失败:', e);
  }
}

// 自动保存文件ID
if (pm.response.code === 200 && pm.request.url.path.includes('/files/upload')) {
  try {
    const response = pm.response.json();
    if (response.data && response.data.fileId) {
      pm.environment.set('FILE_ID', response.data.fileId);
      console.log('文件ID已自动保存:', response.data.fileId);
    }
  } catch (e) {
    console.log('解析文件上传响应失败:', e);
  }
}

// 自动保存Agent ID
if (pm.response.code === 200 && pm.request.url.path.includes('/agents') && pm.request.method === 'POST') {
  try {
    const response = pm.response.json();
    if (response.data && response.data.id) {
      pm.environment.set('AGENT_ID', response.data.id);
      console.log('Agent ID已自动保存:', response.data.id);
    }
  } catch (e) {
    console.log('解析Agent创建响应失败:', e);
  }
}

// 响应时间监控
const responseTime = pm.response.responseTime;
if (responseTime > 5000) {
  console.warn('响应时间过长:', responseTime + 'ms');
}

// 错误响应处理
if (pm.response.code >= 400) {
  console.error('请求失败:', pm.response.code, pm.response.status);
  try {
    const errorResponse = pm.response.json();
    console.error('错误详情:', errorResponse);
  } catch (e) {
    console.error('无法解析错误响应');
  }
}

beforeScript.script:
// 请求前预处理
console.log('开始请求:', pm.request.method, pm.request.url);

// 检查必要参数
if (pm.request.url.path.includes('{{AGENT_ID}}') && !pm.environment.get('AGENT_ID')) {
  console.warn('警告: AGENT_ID 未设置，请求可能失败');
}

if (pm.request.url.path.includes('{{SESSION_ID}}') && !pm.environment.get('SESSION_ID')) {
  console.warn('警告: SESSION_ID 未设置，请求可能失败');
}
